package com.alphalearning.myapplication1;import androidx.appcompat.app.AppCompatActivity;import android.content.Intent;import android.os.Bundle;import android.widget.ImageView;import android.widget.TextView;import com.alphalearning.myapplication1.model.ResultsItem;import com.bumptech.glide.Glide;import org.parceler.Parcels;public class DetailActivity extends AppCompatActivity {ResultsItem dataMovie;TextView tvtitle,tvOverview1;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_detail);        Bundle bundle = getIntent().getBundleExtra(MoviesAdapter.DATAMOVIE);        dataMovie = Parcels.unwrap(bundle.getParcelable(MoviesAdapter.MOVIE));        ImageView ivPos = findViewById(R.id.ivDetail);        Glide.with(DetailActivity.this).load("https://image.tmdb.org/t/p/w500"+dataMovie.getBackdropPath()).into(ivPos);        tvtitle = findViewById(R.id.tvTittle);        tvtitle.setText(dataMovie.getTitle());        tvOverview1 = findViewById(R.id.tvOverview1);        tvOverview1.setText(dataMovie.getOverview());    }}